package com.abdurashidov.roomreference.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.abdurashidov.roomreference.databinding.MyRvItemBindingimport com.abdurashidov.roomreference.db.AppDataBaseimport com.abdurashidov.roomreference.db.MyTransactionclass MyRvAdapter(val list:ArrayList<MyTransaction>, val appDataBase: AppDataBase): RecyclerView.Adapter<MyRvAdapter.Vh>() {    inner class Vh(val rvItem: MyRvItemBinding): RecyclerView.ViewHolder(rvItem.root){        fun onBind(label: MyTransaction){            rvItem.summa.text=label.summa            rvItem.fromCard.text=appDataBase.myDao().getCard(label.from_card_id!!).number.toString()            rvItem.toCard.text=appDataBase.myDao().getCard(label.to_card_id!!).number.toString()            rvItem.date.text=label.date        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): Vh {        return Vh(MyRvItemBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun onBindViewHolder(holder: Vh, position: Int) {        holder.onBind(list[position])    }    override fun getItemCount(): Int {        return list.size    }}